function generateGradientColors(e,t,r){let l=hexToRgb(e),o=hexToRgb(t),n=1/(r-1),i=[];for(let a=0;a<r;a++)i.push(interpolateColor(l,o,n*a));return i.map(rgbToHex)}function hexToRgb(e){let t=parseInt(e.replace("#",""),16);return[t>>16&255,t>>8&255,255&t]}function rgbToHex(e){return"#"+e.map(e=>e.toString(16).padStart(2,"0")).join("")}function interpolateColor(e,t,r){arguments.length<3&&(r=.5);let l=e.slice();for(let o=0;o<3;o++)l[o]=Math.round(l[o]+r*(t[o]-e[o]));return l}function applyGradientToLiItems(e,t,r){let l=e.querySelectorAll("li"),o=generateGradientColors(t,r,l.length);l.forEach((e,t)=>{e.style.paddingLeft="0.5rem",e.style.borderLeftWidth="5px",e.style.borderLeftStyle="solid",e.style.borderLeftColor=o[t],e.style.marginTop="1rem",0===t&&(e.style.marginTop="0")})}function applyGradientToAllLists(){let e=document.querySelectorAll("ul.gradient-list");e.forEach(e=>{applyGradientToLiItems(e,"#00E1FF","#FF1E00")})}export{applyGradientToAllLists};
